import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [summary, setSummary] = useState("");
  const [summaries, setSummaries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Fetch stored summaries from the backend
  const fetchSummaries = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/summaries");
      setSummaries(response.data);
    } catch (err) {
      console.error("Error fetching summaries:", err);
      setError("Failed to fetch summaries.");
    }
  };

  // Fetch stored summaries when the component mounts
  useEffect(() => {
    fetchSummaries();
  }, []);

  // Handle summarization request
  const handleSummarize = async () => {
    if (!text.trim()) {
      setError("Please enter medical text to summarize.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/summarize", { text });
      setSummary(response.data.summary);
      setText(""); // Clear input field
      fetchSummaries(); // Refresh stored summaries
    } catch (err) {
      console.error("Error generating summary:", err);
      setError("Failed to generate summary. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-4">Medical Text Summarization</h1>

      {error && <p className="text-red-500 text-center">{error}</p>}

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Enter medical text here..."
        rows="5"
        className="w-full p-3 border border-gray-300 rounded-md shadow-sm"
      ></textarea>

      <button
        onClick={handleSummarize}
        className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-md mt-3"
        disabled={loading}
      >
        {loading ? (
          <span className="flex justify-center">
            <svg className="animate-spin h-5 w-5 mr-3 text-white" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="white" strokeWidth="4"></circle>
              <path className="opacity-75" fill="white" d="M4 12a8 8 0 018-8v8z"></path>
            </svg>
            Processing...
          </span>
        ) : (
          "Summarize"
        )}
      </button>

      {summary && (
        <div className="mt-5 p-4 bg-white shadow-md rounded-md">
          <h2 className="text-xl font-semibold">Summary:</h2>
          <p className="text-gray-700">{summary}</p>
        </div>
      )}

      <h2 className="text-xl font-semibold mt-6">Past Summaries</h2>
      <ul className="mt-3 space-y-3">
        {summaries.map((item, index) => (
          <li key={index} className="p-3 bg-white shadow-md rounded-md border border-gray-200">
            <strong>Original:</strong> {item.text} <br />
            <strong>Summary:</strong> {item.summary} <br />
            <span className="text-gray-500 text-sm">Date: {new Date(item.createdAt).toLocaleString()}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
