import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSummarize = async () => {
    if (!text.trim()) {
      setError("Please enter medical text to summarize.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/summarize", { text });
      setSummary(response.data.summary);
      setText(""); // Clear input field
    } catch (err) {
      console.error("Error generating summary:", err);
      setError("Failed to generate summary. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="app-container">
      <h1 className="title">Medical Text Summarization</h1>

      {error && <p className="error">{error}</p>}

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Enter medical text here..."
        rows="5"
        className="input-box"
      ></textarea>

      <button onClick={handleSummarize} className="button" disabled={loading}>
        {loading ? "Summarizing..." : "Summarize"}
      </button>

      {loading && <p className="loading">Processing summary...</p>}

      {summary && (
        <div className="summary-box">
          <h2>Summary:</h2>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}

export default App;
