import React, { useState } from "react";
import axios from "axios";

function App() {
  const [text, setText] = useState("");
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // ‚úÖ Function to send text to FastAPI backend
  const handleSummarize = async () => {
    if (!text.trim()) {
      setError("Please enter medical text to summarize.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      // ‚úÖ Make API request to FastAPI
      const response = await axios.post("http://127.0.0.1:8000/summarize", { text });

      console.log("üü¢ API Response:", response.data);
      setSummary(response.data.summary);
    } catch (err) {
      console.error("‚ùå Error generating summary:", err);
      setError("Failed to generate summary. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-4">Medical Text Summarization</h1>

      {error && <p className="text-red-500 text-center">{error}</p>}

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Enter medical text here..."
        rows="5"
        className="w-full p-3 border border-gray-300 rounded-md shadow-sm"
      ></textarea>

      <button
        onClick={handleSummarize}
        className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-md mt-3"
        disabled={loading}
      >
        {loading ? "Summarizing..." : "Summarize"}
      </button>

      {loading && <p className="text-center text-blue-500 mt-3">Processing summary...</p>}

      {summary && (
        <div className="mt-5 p-4 bg-white shadow-md rounded-md">
          <h2 className="text-xl font-semibold">Summary:</h2>
          <p className="text-gray-700">{summary}</p>
        </div>
      )}
    </div>
  );
}

export default App;
